mapper.select:
select l.line_id, l.issue_id, l.item_id, l.item_code, l.item_name, l.specification, l.unit_of_measure, l.unit_name, l.quantity_issued, sum(d.quantity) as quantity, l.batch_id, l.batch_code, l.remark, l.attr1, l.attr2, l.attr3, l.attr4, l.create_by, l.create_time, l.update_by, l.update_time
from wm_outsource_issue_line l
left join wm_outsource_issue_detail d on l.line_id = d.line_id

     and l.issue_id = #{issueId}
     and l.item_id = #{itemId}
     and l.item_code = #{itemCode}
     and l.item_name like concat('%', #{itemName}, '%')
     and l.specification = #{specification}
     and l.unit_of_measure = #{unitOfMeasure}
    and l.unit_name = #{unitName}
     and l.quantity_issued = #{quantityIssued}
     and l.batch_id = #{batchId}
     and l.batch_code = #{batchCode}

group by l.line_id, l.issue_id, l.item_id, l.item_code, l.item_name, l.specification, l.unit_of_measure, l.unit_name, l.quantity_issued, l.batch_id, l.batch_code, l.remark, l.attr1, l.attr2, l.attr3, l.attr4, l.create_by, l.create_time, l.update_by, l.update_time
order by l.create_time desc



     and l.issue_id = #{issueId}
     and l.item_id = #{itemId}
     and l.item_code = #{itemCode}
     and l.item_name like concat('%', #{itemName}, '%')
     and l.specification = #{specification}
     and l.unit_of_measure = #{unitOfMeasure}
    and l.unit_name = #{unitName}
     and l.quantity_issued = #{quantityIssued}
     and l.batch_id = #{batchId}
     and l.batch_code = #{batchCode}

order by create_time desc

mapper.update:
update wm_outsource_issue_line
issue_id = #{issueId}, item_id = #{itemId}, item_code = #{itemCode}, item_name = #{itemName}, specification = #{specification}, unit_of_measure = #{unitOfMeasure}, unit_name = #{unitName}, quantity_issued = #{quantityIssued}, batch_id = #{batchId}, batch_code = #{batchCode}, remark = #{remark}, attr1 = #{attr1}, attr2 = #{attr2}, attr3 = #{attr3}, attr4 = #{attr4}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where line_id = #{lineId}
