mapper.select:


     and sales_id = #{salesId}
     and material_stock_id = #{materialStockId}
     and item_id = #{itemId}
     and item_code = #{itemCode}
     and item_name like concat('%', #{itemName}, '%')
     and specification = #{specification}
     and unit_of_measure = #{unitOfMeasure}
    and unit_name = #{unitName}
     and quantity_sales = #{quantitySales}
     and batch_id = #{batchId}
     and batch_code = #{batchCode}
     and oqc_check = #{oqcCheck}
     and oqc_id = #{oqcId}
     and oqc_code = #{oqcCode}
     and quality_status = #{qualityStatus}
     and remark like concat('%', #{remark}, '%')

order by create_time desc

select l.line_id, l.sales_id, l.item_id, l.item_code, l.item_name, l.specification, l.unit_of_measure, l.unit_name, l.quantity_sales,sum(d.quantity) as quantity, l.batch_id, l.batch_code, l.oqc_check,l.oqc_id,l.oqc_code, l.quality_status, l.remark, l.attr1, l.attr2, l.attr3, l.attr4, l.create_by, l.create_time, l.update_by, l.update_time
from wm_product_sales_line l
left join wm_product_sales_detail d on l.line_id = d.line_id

     and l.sales_id = #{salesId}
     and l.material_stock_id = #{materialStockId}
     and l.item_id = #{itemId}
     and l.item_code = #{itemCode}
     and l.item_name like concat('%', #{itemName}, '%')
     and l.specification = #{specification}
     and l.unit_of_measure = #{unitOfMeasure}
    and l.unit_name = #{unitName}
     and l.quantity_sales = #{quantitySales}
     and l.batch_id = #{batchId}
     and l.batch_code = #{batchCode}
     and l.oqc_check = #{oqcCheck}
     and l.oqc_id = #{oqcId}
     and l.oqc_code = #{oqcCode}
     and l.quality_status = #{qualityStatus}
     and l.remark like concat('%', #{remark}, '%')

group by l.line_id, l.sales_id, l.item_id, l.item_code, l.item_name, l.specification, l.unit_of_measure, l.unit_name, l.quantity_sales, l.batch_id, l.batch_code, l.oqc_check,l.oqc_id,l.oqc_code, l.quality_status, l.remark, l.attr1, l.attr2, l.attr3, l.attr4, l.create_by, l.create_time, l.update_by, l.update_time
order by l.create_time desc



