mapper.select:


     and transfer_id = #{transferId}
     and material_stock_id = #{materialStockId}
     and item_id = #{itemId}
     and item_code = #{itemCode}
     and item_name like concat('%', #{itemName}, '%')
     and specification = #{specification}
     and unit_of_measure = #{unitOfMeasure}
    and unit_name = #{unitName}
     and quantity_transfer = #{quantityTransfer}
     and batch_id = #{batchId}
     and batch_code = #{batchCode}
     and warehouse_id = #{warehouseId}
     and warehouse_code = #{warehouseCode}
     and warehouse_name like concat('%', #{warehouseName}, '%')
     and location_id = #{locationId}
     and location_code = #{locationCode}
     and location_name like concat('%', #{locationName}, '%')
     and area_id = #{areaId}
     and area_code = #{areaCode}
     and area_name like concat('%', #{areaName}, '%')


order by create_time desc

select l.line_id, l.transfer_id, l.material_stock_id, l.item_id, l.item_code, l.item_name, l.specification, l.unit_of_measure, l.unit_name,  l.quantity_transfer, sum(d.quantity) as quantity, l.warehouse_id, l.warehouse_code, l.warehouse_name, l.location_id, l.location_code, l.location_name, l.area_id, l.area_code, l.area_name, l.batch_id, l.batch_code, l.remark, l.attr1, l.attr2, l.attr3, l.attr4, l.create_by, l.create_time, l.update_by, l.update_time
from wm_transfer_line l
left join wm_transfer_detail d on l.line_id = d.line_id

     and l.transfer_id = #{transferId}
     and l.material_stock_id = #{materialStockId}
     and l.item_id = #{itemId}
     and l.item_code = #{itemCode}
     and l.item_name like concat('%', #{itemName}, '%')
     and l.specification = #{specification}
     and l.unit_of_measure = #{unitOfMeasure}
    and l.unit_name = #{unitName}
     and l.quantity_transfer = #{quantityTransfer}
     and l.batch_id = #{batchId}
     and l.batch_code = #{batchCode}
     and l.warehouse_id = #{warehouseId}
     and l.warehouse_code = #{warehouseCode}
     and l.warehouse_name like concat('%', #{warehouseName}, '%')
     and l.location_id = #{locationId}
     and l.location_code = #{locationCode}
     and l.location_name like concat('%', #{locationName}, '%')
     and l.area_id = #{areaId}
     and l.area_code = #{areaCode}
     and l.area_name like concat('%', #{areaName}, '%')

group by l.line_id, l.transfer_id, l.material_stock_id, l.item_id, l.item_code, l.item_name, l.specification, l.unit_of_measure, l.unit_name,  l.quantity_transfer, l.warehouse_id, l.warehouse_code, l.warehouse_name, l.location_id, l.location_code, l.location_name, l.area_id, l.area_code, l.area_name, l.batch_id, l.batch_code, l.remark, l.attr1, l.attr2, l.attr3, l.attr4, l.create_by, l.create_time, l.update_by, l.update_time
order by l.create_time desc



     and l.transfer_id = #{transferId}
     and l.material_stock_id = #{materialStockId}
     and l.item_id = #{itemId}
     and l.item_code = #{itemCode}
     and l.item_name like concat('%', #{itemName}, '%')
     and l.specification = #{specification}
     and l.unit_of_measure = #{unitOfMeasure}
    and l.unit_name = #{unitName}
