mapper.update:
update wm_transfer_line
transfer_id = #{transferId}, material_stock_id = #{materialStockId}, item_id = #{itemId}, item_code = #{itemCode}, item_name = #{itemName}, specification = #{specification}, unit_of_measure = #{unitOfMeasure}, unit_name = #{unitName}, quantity_transfer = #{quantityTransfer}, batch_id = #{batchId}, batch_code = #{batchCode}, warehouse_id = #{warehouseId}, warehouse_code = #{warehouseCode}, warehouse_name = #{warehouseName}, location_id = #{locationId}, location_code = #{locationCode}, location_name = #{locationName}, area_id = #{areaId}, area_code = #{areaCode}, area_name = #{areaName}, quality_status = #{qualityStatus}, remark = #{remark}, attr1 = #{attr1}, attr2 = #{attr2}, attr3 = #{attr3}, attr4 = #{attr4}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where line_id = #{lineId}
